const section=document.querySelectorAll(".game-object__section"),button=document.querySelector(".game-information__btn");let roundValue=document.getElementById("round"),randomArray=[],clickedArray=[];function addElemRandom(){const e=section[Math.floor(Math.random()*section.length)];randomArray.push(e)}function compareRandomAndClickedArr(){return randomArray.length===clickedArray.length&&clickedArray.every((e,n)=>e===randomArray[n])}function nextRound(){roundValue.textContent=clickedArray.length,clickedArray=[]}function endGame(){roundValue.textContent=0,randomArray=[],clickedArray=[]}button.addEventListener("click",()=>{button.disabled=!0,addElemRandom(),randomArray.forEach((e,n)=>{setTimeout(()=>{e.classList.add("animation"),setTimeout(()=>{e.classList.remove("animation")},500)},800*n)})}),section.forEach(e=>{e.addEventListener("click",()=>{if(clickedArray.push(e),compareRandomAndClickedArr())nextRound(),button.disabled=!1;else{if(clickedArray.length!==randomArray.length)return;endGame(),button.disabled=!1}})});